import React from 'react';
import $ from 'jquery';

let modal = document.querySelector(".modal")
    let closeBtn = document.querySelector(".close-btn")
    let parentPortalBtn = document.querySelector("#parentPortalBtn")

    $(window).on('load',() => {
        const loadModal = () => {
            modal.style.display = "block"
        }
        window.setTimeout(loadModal, 3000);
    });

    $(window).on('click', (e) => {
        if(e.target == modal){
            modal.style.display = "none"
        }
    });

    closeBtn.onclick = () => {
        modal.style.display = "none"
    }

    parentPortalBtn.onclick =  () => {
        modal.style.display = "block"
    }

const Modal = () => {

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close-btn">&times;</span>
                <div className="container text-center">
                    <form>
                        <div className="row">
                            <div className="col-md-12 text-center">
                                Please enter your password
                            </div>
                        </div>
                        <div className="row" style={{ paddingBottom: "40px" }}>
                            <div className="col-sm-12" style={{ padding: "20px" }}>
                                <input id="passwordInput" className="form-input" placeholder="Password" style={{ width: "80%" }}/>
                            </div>
                            <div className="col-sm-12">
                                <button id="passwordBtn" className="sign-up-button" style={{ width: "80%" }}>Sign Up</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Modal
